mapper.update:
update stu_ability_score
academy = #{academy}, academic_score = #{academicScore}, competition_score = #{competitionScore}, activity_score = #{activityScore}, lecture_score = #{lectureScore}, tutorial_score = #{tutorialScore},
where student_id = #{studentId}

mapper.select:
SELECT student_id, ROUND( COALESCE(SUM(gpa * credit) / NULLIF(SUM(credit), 0), 0), 2 ) AS raw_gpa FROM ( SELECT s.student_id, s.gpa, COALESCE(c.credit, 0) AS credit, ROW_NUMBER() OVER ( PARTITION BY s.student_id, s.course_code ORDER BY CASE s.score_type WHEN '正考' THEN 1 WHEN '重修' THEN 2 WHEN '补考' THEN 3 ELSE 4 END, s.upload_time DESC ) AS rn FROM stu_score s INNER JOIN stu_course c ON s.course_code = c.course_code AND c.course_category = '必修' WHERE s.gpa IS NOT NULL
AND s.student_id IN
#{id}
) t WHERE rn = 1 GROUP BY student_id

mapper.update:
UPDATE stu_ability_score a JOIN (
SELECT #{item.studentId} AS student_id, #{item.academicScore} AS academic_score
) t USING(student_id) SET a.academic_score = COALESCE(t.academic_score, 0)
