mapper.select:
SELECT competition_id AS id, competition_name, -- 竞赛名称 student_id, -- 学号 award_date, -- 获奖日期 semester, -- 学期 competition_level, -- 竞赛级别 award_level, -- 奖项级别 proof_material, -- 证明材料 audit_status, -- 审核状态 audit_time, -- 审核时间 audit_remark -- 审核备注 FROM stu_competition_record
AND competition_name LIKE CONCAT('%', #{competitionName}, '%')
AND student_id = #{studentId}
AND award_date = #{awardDate}
