mapper.select:
SELECT * FROM stu_lec_report
AND student_id = #{studentId}
AND semester = #{semester}
ORDER BY CASE audit_status WHEN 3 THEN 0 -- 未审核排最前 WHEN 1 THEN 1 -- 未审核排最前 WHEN 0 THEN 2 -- 未通过其次 WHEN 2 THEN 3 -- 已通过最后 ELSE 4 -- 其他状态（如果有） END, report_date ASC

SELECT * FROM stu_lec_report

     and student_id = #{studentId}
     and student_name like concat('%', #{studentName}, '%')
     and report_title = #{reportTitle}
     and reporter = #{reporter}
     and report_date = #{reportDate}
     and report_location = #{reportLocation}
     and report_content = #{reportContent}
     and report_link = #{reportLink}
     and report_feeling = #{reportFeeling}
     and lecture_poster = #{lecturePoster}
     and report_picture = #{reportPicture}
     and report_admit_time = #{reportAdmitTime}
     and audit_status = #{auditStatus}
     and audit_time = #{auditTime}
     and audit_remark = #{auditRemark}
     and semester = #{semester}
    and audit_status != 3

ORDER BY
CASE audit_status
WHEN 0 THEN 0    -- 未审核排最前
WHEN 2 THEN 1    -- 未通过其次
WHEN 1 THEN 2    -- 已通过最后
ELSE 3
END,
report_date ASC

mapper.update:
UPDATE stu_lec_report
report_title = #{reportTitle}, reporter = #{reporter}, report_date = #{reportDate}, report_content = #{reportContent}, report_location = #{reportLocation}, report_link = #{reportLink}, report_feeling = #{reportFeeling}, lecture_poster = #{lecturePoster}, report_picture = #{reportPicture}, audit_status = #{auditStatus}, audit_time = #{auditTime}, audit_remark = #{auditRemark}, report_admit_time = #{reportAdmitTime},
where report_id = #{reportId}
