mapper.select:
SELECT
smr.*,
si.student_name,
t.tutor_name,
t.tutor_id
FROM
stu_mentorship_record smr
LEFT JOIN
stu_info si ON smr.student_id = si.student_id
LEFT JOIN
stu_mentor_info smi ON smr.student_id = smi.student_id
LEFT JOIN
tutors t ON smi.tutor_id = t.tutor_id

    AND si.student_id = #{studentId}
    AND si.student_Name = #{studentName}
    AND t.tutor_id = #{tutorId}
    AND smr.guidance_topic = #{guidanceTopic}
    AND smr.guidance_location = #{guidanceLocation}
    AND smr.guidance_time = #{guidanceTime}
    AND smr.summary_file_path = #{summaryFilePath}
    AND smr.photo_paths = #{photoPaths}
    AND smr.semester = #{semester}
    AND smr.submit_time = #{submitTime}
    AND smr.student_comment = #{studentComment}
    AND smr.audit_status = #{auditStatus}
    AND smr.audit_time = #{auditTime}
    AND smr.audit_remark = #{auditRemark}

ORDER BY
CASE audit_status
WHEN '未提交' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未审核排最前
WHEN '未审核' THEN 2    -- 未通过其次
WHEN '已通过' THEN 3    -- 已通过最后
ELSE 4                 -- 其他状态（如果有）
END,
submit_time DESC

SELECT
smr.*,
si.student_name,
t.tutor_name,
t.tutor_id
FROM
stu_mentorship_record smr
LEFT JOIN
stu_info si ON smr.student_id = si.student_id
LEFT JOIN
stu_mentor_info smi ON smr.student_id = smi.student_id
LEFT JOIN
tutors t ON smi.tutor_id = t.tutor_id

    AND si.student_id = #{studentId}
    AND si.student_Name = #{studentName}
    AND t.tutor_id = #{tutorId}
    AND smr.guidance_topic = #{guidanceTopic}
    AND smr.guidance_location = #{guidanceLocation}
    AND smr.guidance_time = #{guidanceTime}
    AND smr.summary_file_path = #{summaryFilePath}
    AND smr.photo_paths = #{photoPaths}
    AND smr.semester = #{semester}
    AND smr.submit_time = #{submitTime}
    AND smr.student_comment = #{studentComment}
    AND smr.audit_status = #{auditStatus}
    AND smr.audit_time = #{auditTime}
    AND smr.audit_remark = #{auditRemark}
            AND audit_status != '未提交'

ORDER BY
CASE audit_status
WHEN '未审核' THEN 0    -- 未审核排最前
WHEN '未通过' THEN 1    -- 未通过其次
WHEN '已通过' THEN 2    -- 已通过最后
ELSE 3                 -- 其他状态（如果有）
END,
submit_time DESC

mapper.update:
update stu_mentorship_record
student_id = #{studentId}, guidance_topic = #{guidanceTopic}, guidance_location = #{guidanceLocation}, guidance_time = #{guidanceTime}, summary_file_path = #{summaryFilePath}, photo_paths = #{photoPaths}, semester = #{semester}, submit_time = #{submitTime}, student_comment = #{studentComment}, audit_status = #{auditStatus}, audit_time = #{auditTime}, audit_remark = #{auditRemark},
where record_id = #{recordId}
