mapper.insert:
INSERT INTO stu_score (
    student_id, course_code, course_name, course_category,
    credit, academy, semester, score_value, gpa, score_type
) VALUES (
             #{studentId}, #{courseCode}, #{courseName}, #{courseCategory},
             #{credit}, #{academy}, #{semester}, #{scoreValue}, #{gpa}, #{scoreType}
         )

mapper.update:
UPDATE stu_score
student_id = #{studentId}, course_code = #{courseCode}, course_name = #{courseName}, course_category = #{courseCategory}, credit = #{credit}, academy = #{academy}, semester = #{semester}, score_value = #{scoreValue}, gpa = #{gpa}, score_type = #{scoreType}
WHERE score_id = #{scoreId}

mapper.select:
SELECT 
FROM stu_score

    AND student_id = #{studentId}
    AND course_code = #{courseCode}
    AND semester = #{semester}

ORDER BY upload_time DESC

SELECT 
FROM stu_score
WHERE student_id = #{studentId}
AND semester = #{semester}
