SELECT u.id, user_name, academy, systemMajor, change_adress, change_major, u.change_major_type, after_change_adress, after_change_major, after_change_major_type
FROM user_questionnaire_answers u
join student s
on s.studentId = u.user_name
--         WHERE questionnaire_id = 1

    AND id = #{id}
    AND user_name LIKE CONCAT('%', #{userName}, '%')
    AND academy = #{academy}
    AND systemMajor = #{systemMajor}
    AND change_adress = #{changeAdress}
    AND change_major = #{changeMajor}
    AND u.change_major_type = #{changeMajorType}
    AND after_change_adress = #{afterChangeAdress}
    AND after_change_major = #{afterChangeMajor}
    AND after_change_major_type = #{afterChangeMajorType}
